INSERT INTO `wp_wc_admin_notes` VALUES('1', 'wc-admin-welcome-note', 'info', 'en_US', 'New feature(s)', 'Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2019-10-15 07:52:50', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('2', 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2019-10-15 07:52:50', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('3', 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2019-10-17 07:59:19', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('4', 'wc-admin-store-notice-giving-feedback', 'info', 'en_US', 'Review your experience', 'If you like WooCommerce Admin please leave us a 5 star rating. A huge thanks in advance!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2019-10-18 08:15:54', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('5', 'wc-admin-facebook-extension', 'info', 'en_US', 'Market on Facebook', 'Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.', 'thumbs-up', '{}', 'actioned', 'woocommerce-admin', '2019-10-18 08:15:54', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('6', 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help WooCommerce improve with usage tracking', 'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"https://lms.engaging-people.com/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2019-11-23 07:54:24', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('7', 'wc-admin-orders-milestone', 'info', 'en_US', 'First order', 'Congratulations on getting your first order from a customer! Learn how to manage your orders.', 'trophy', '{}', 'unactioned', 'woocommerce-admin', '2019-12-06 10:53:13', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('8', 'wc-admin-new-sales-record', 'info', 'en_US', 'New sales record!', 'Woohoo, December 6th was your record day for sales! Net revenue was 10,00€ beating the previous record of 5,00€ set on October 21st.', 'trophy', '{\"old_record_date\":\"2019-10-21\",\"old_record_amt\":5,\"new_record_date\":\"2019-12-06\",\"new_record_amt\":10}', 'unactioned', 'woocommerce-admin', '2019-12-07 07:52:52', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('9', 'wc-admin-store-notice-setting-moved', 'info', 'en_US', 'Looking for the Store Notice setting?', 'It can now be found in the Customizer.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-02-11 14:00:48', NULL, '0');
INSERT INTO `wp_wc_admin_notes` VALUES('10', 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, product updates, and inspiration', 'We\'re here for you — get tips, product updates, and inspiration straight to your mailbox.', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-02-14 08:07:35', NULL, '0');
